<script>
    document.addEventListener('DOMContentLoaded', () => {
        document.querySelectorAll('.accordion').forEach(accordion => {
            const tabs = accordion.querySelectorAll('.accordion-tab');
            const contents = accordion.querySelectorAll('.accordion-content');

            tabs.forEach(tab => {
                tab.addEventListener('click', () => {
                    const isMobile = window.matchMedia('(max-width: 768px)').matches;
                    const targetId = tab.getAttribute('data-accordion-target');
                    const targetContent = accordion.querySelector(`#${targetId}`);

                    // Reset only within this accordion
                    tabs.forEach(t => t.classList.remove('active'));
                    contents.forEach(content => (content.style.display = 'none'));

                    // Activate clicked tab
                    tab.classList.add('active');

                    // Show content
                    if (targetContent) {
                        if (isMobile) {
                            targetContent.style.display = 'block';
                            targetContent.scrollIntoView({ behavior: 'smooth' });
                        } else {
                            targetContent.style.display = 'block';
                        }
                    }
                });
            });

            // Default: Open the first tab in each accordion
            if (tabs.length > 0 && contents.length > 0) {
                tabs[0].classList.add('active');
                contents[0].style.display = 'block';
            }
        });
    });
</script>

